import React ,{useState,useEffect} from 'react';
import {Text,
  View,
  StyleSheet ,
    Image,
    Picker,
    ScrollView,
    TouchableOpacity,
    ActivityIndicator,
    Dimensions,
    Alert

} from 'react-native';
import { LinearGradient,Button } from 'expo-linear-gradient';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import {useSelector,useDispatch} from 'react-redux';
import * as subjectdetaillist from '../store/action/subjectlist';
import * as    donotallow from "../store/action/auth";
import Const from '../components/constant';


import Ionicons from 'react-native-vector-icons/Ionicons';
import SubShow from '../components/subjectshow';
//import { ActivityIndicator } from 'react-native-paper';
const Home=({navigation})=>{
   // const name=navigation.getParam('username')
    //console.log(name)

   

    const  dispatch=useDispatch()  ;
    
    const usernamelogged=useSelector(state=>state.Authname.usernamee)
    const subjectarray=useSelector(state=>state.Sub.subjectlist)
    const classarray=useSelector(state=>state.Sub.classlis)
  
    
    //console.log("this is class list $"+ classarray[0])
   console.log("this is the user name"+ usernamelogged)
    const [visible, setvisibility] = useState(false);
    const [selectedValueofsub,setSelectedValueofsub]=useState(1)
    const [error,seterror]=useState();
    const [isloadingm,setisloadingm]=useState(false)
    const [isloadingmm,setisloadingmm]=useState(false)
    const [selectedValue, setSelectedValue] = useState(1);
    
    const SubjectList= async()=>{
        seterror(null)
        setisloadingm(true)
        try{
          await dispatch(subjectdetaillist.subject_list(Const.school_id,1))
          console.log("executed")
          setisloadingm(false) 
        }catch(err){
          seterror(error.message)
          
        }
      }  
      const doonotallow=async()=>{
        seterror(null)
        setisloadingmm(true)
        try{
       await   dispatch(donotallow.donotallowuser(null))
        }catch(error){
          seterror(error.message)
          setisloadingmm(false)
        }
      }
      const Classslistt= async()=>{
        seterror(null)
        //setisloadingm(true)
        try{
          await dispatch(subjectdetaillist.classlistALL(Const.school_id,1))
          console.log("executed")
          //setisloadingm(false) 
        }catch(err){
          seterror(err.message)
          
        }
      }  
      useEffect(() => {
       
        if (error) {
          Alert.alert('An Error Occurred!', error, [{ text: 'Okay' }]);
        }
      SubjectList();
      Classslistt();
      //  Classslistt();
      }, [error]);
   
   const classall=useSelector(state=>state.Classlist.classlis);
   console.log("this the required classlist "+classall)

    return(
      <ScrollView>
        <View style={styles.main}>
              <View style={styles.usertop}>
              <LinearGradient
           // Background Linear Gradient
               colors={['#007E59', '#007E59']}
               start={[.9,.0]}
                style={{
                  position: 'absolute',
                  left: 0,
                  right: 0,
                  top: 0,
                  height: 200,
                  }}
                 />
                  <View style={styles.user}>
                    <View style={styles.logo}>
                      <Image style={styles.image} source={require('../assets/drawable-hdpi/image5.png')}/>
                    </View>
                    <View style={styles.username}>
                      <Text style={styles.usernamechild}>{usernamelogged}</Text>
                    </View>
                    <View style={styles.logout}>
                     {isloadingmm?<ActivityIndicator/>:
                         <Text 
                         style={styles.logoutchild}
                         onPress={doonotallow}
                         >Logout</Text>}
                   </View>
                  </View>
                  <View style={{ alignItems:'center'}}>
                     <Text style={{  
                        fontFamily:"Poppins-Bold",
                        fontSize:20,
                        color:"#FFFFFF"
                        }} >
                      Thought of the day</Text>

                  </View >
                   <View style={{alignItems:"center",
                   justifyContent:"center",
                   marginHorizontal:10
                   }}>
                     <Text style={{
                       fontFamily:"Poppins-Regular",
                       color:"#FFFFFF"
                     }}
                     numberOfLines={3}
                     >"It was popularised in the 1960s containin"</Text>

                   </View>

                  </View>
                   <View style={styles.secondbox}>
                       <View style={styles.notification}>
                          <View style={styles.nott}>
                            <LinearGradient
                               // Background Linear Gradient
                              colors={['#007E59', '#007E59']}
                              start={[.9,.0]}
                              style={{
                                position: 'absolute',
                                left: 0,
                                right: 0,
                                top: 0,
                                height: 40,
                              }}/>
                                  <View><Ionicons name="md-notifications" size={24} color="#0bbd41" /></View>
                                  <View><Text style={styles.notificationc}>Notification</Text></View>
                          </View>
                          <View style={styles.nottmsg}>
                              <Text style={styles.msssg}>Cras gravida bibend</Text>
                          </View>
                        </View>
                 
                       <View style={styles.subb}>
                           <View>
                               <Text style={styles.subshow}>Subject    </Text>
                           </View>
                           <View>
                               <Text style={styles.subcount}>(06)</Text>
                           </View>
                           <View style={styles.selectclass}>
                              <Picker selectedValue={selectedValue}
                                style={{height:40 }}
                                onValueChange={(itemValue, itemIndex) => setSelectedValue(itemValue)}>
                                {classall.map((dat,index)=> <Picker.Item  key={'class-'+index} label={dat.name} value={dat.id} />)}
                              </Picker>
                           </View>
                       </View>
                   </View>
            <ScrollView style={{marginBottom:200}}>
             {subjectarray.map((data,index) => 
                isloadingm?<ActivityIndicator key={'indicator-'+index}/>:
                <TouchableOpacity key={'subject-'+index} onPress={()=>navigation.navigate("MathScreen",{
                subject_id:data.sub_id,
                sub_namee:data.sub_name
              })}>
                <SubShow
                  source={require("../assets/drawable-hdpi/image3.png")}
                  subject={data.sub_name}
                  noofnotes={12}
                  noofvideo={6}
                  timing="11:00 AM"
                  nextlivesession="Next live session 02:00PM"
                  style={{backgroundColor:"#94E2FF33",
                    borderColor:"#0088BA" ,
                    borderWidth:.2,
                    marginBottom:5,
                  }}
                  subcolor="#0088BA"
                  notecolor="#0088BA"
                  timingcolor="#0088BA"
                  livesessioncolor="#00C0D2"
                />
              </TouchableOpacity>
            )}
            </ScrollView>
        </View>
      </ScrollView>
    )
}
const styles=StyleSheet.create({
        main:{
           flex:1,
            backgroundColor:"white",
            flex:1
        },
        usertop:{
            height:200,
            paddingTop:20
        },
        user:{
            flexDirection:"row",
            
            alignItems:"center",
            justifyContent:"space-around",
            
            height:70,
        
        },
        logo:{
            backgroundColor:"white",
            height:30,
            width:30,
            justifyContent:"center",
            alignItems:'center',
           marginLeft:10,
           borderRadius:40,
           marginTop:20
            
        },
        image:{
            width:30,
            height:30,
            borderRadius:40,
            borderWidth:2,
            borderColor:"#8D0EDB"
        },
        username:{
           marginLeft:5,
           marginTop:20,
           paddingRight:30
        },
        logout:{
           marginLeft:'30%',
        
           marginTop:20,
           marginRight:30
           

        },
        usernamechild:{
            color:"#FFFFFF",
