import React ,{useEffect}from 'react';
import {Text,View,StyleSheet,Button} from 'react-native';
//import { Ionicons } from '@expo/vector-icons';

import {Ionicons} from '@expo/vector-icons';
import { LinearGradient } from 'expo-linear-gradient';
import * as Notifications from 'expo-notifications';
import * as Permissions from 'expo-permissions';
Notifications.setNotificationHandler({
    handleNotification: async ()=>{
        return{
            shouldShowAlert:true,
            shouldPlaySound:true,
            shouldSetBadge:true
        }
    }
})
const More=({navigation})=>{
    useEffect(()=>{
      Permissions.getAsync(Permissions.NOTIFICATIONS).then((statusObj)=>{
          if(statusObj.status!=="granted"){
              return Permissions.askAsync(Permissions.Notifications);
          }
          return statusObj;
      })
      .then((statusObj)=>{
          if(statusObj.status!=="granted"){
              return
          }
      })
    },[])
    useEffect(()=>{
      const backgroundsubscriptions=Notifications.addNotificationResponseReceivedListener(respose=>{
          console.log(respose)
      })

      const foregroundsubscriptions=Notifications.addNotificationReceivedListener((notification)=>
        {
            console.log(notification);
        })
        return ()=>{
            foregroundsubscriptions.remove();
            backgroundsubscriptions.remove();
        }
    },[])
    const TriggerNotification=()=>
               {
        Notifications.scheduleNotificationAsync({
            content:{
                title:"brijesh",
                body:"this the local notification",
                sound:true,
                vibrate:true,
              
            },
            trigger:{
                   seconds:7
            }
        })
    }
    return(
        <View style={styles.main}>
            <Text style={{fontSize:20,}}>This is More Screen</Text>
            <Button title="get notification" onPress={TriggerNotification}/>
        </View>
    )
}
const styles=StyleSheet.create({
        main:{
            alignItems:"center",
            justifyContent:"center",
            backgroundColor:"white",
            flex:1
        },
     
});
export default More;
