import React,{useState,useEffect} from 'react';
import {Text,View,StyleSheet,
    TextInput,Button, 
    Alert,
    ActivityIndicator,
    TouchableOpacity,
    ImageBackground
} from 'react-native';
import {useSelector,useDispatch} from 'react-redux';
import * as Forgatt from '../store/action/forgatepassword';
import { LinearGradient } from 'expo-linear-gradient';

const Resetpassword=({navigation})=>{

    const dispatch=useDispatch()
    const [enteredotp,setenteredotp]=useState("");
    const [enteredpassword,setenteredpassword]=useState("");
    const [enteredpasswordagain,setenteredpasswordagain]=useState("");
    const [error,seterror]=useState();
    const [isloadingm,setisloadingm]=useState(false)
    const [isloading,setisloading]=useState(false)
    const student_id=useSelector(state=>state.Fog.student_id)
    console.log( "this is the recieved student id"+ student_id)
    const otphandler=(enteredText)=>{
        setenteredotp(enteredText)
    }
    const passwordhandler=(enteredText)=>{
        setenteredpassword(enteredText)
    }
    const passwordhandleragain=(enteredText)=>{
        setenteredpasswordagain(enteredText)
    }
    const resetpass= async()=>{
        seterror(null)
        setisloadingm(true)
        try{
          await dispatch(Forgatt.resetforgating(student_id,enteredotp,enteredpassword,enteredpasswordagain))
          console.log("changed successsfully")
       
        }catch(error){
          seterror(error.message)
            setisloadingm(false) 
        }
      }  
      useEffect(() => {
        
        if (error) {
          Alert.alert('An Error Occurred!', error, [{ text: 'Okay' }]);
        }

      }, [error]);
    return(
        // <View style={styles.main}>
        //     <Text>otp</Text>
        //        <TextInput 
        //       style={styles.textinputstyle}
        //        placeholder="otp"
        //         keyboardtype="default"
                
        //         onChangeText={otphandler}
        //         value={enteredotp}
        //         />
        //         <Text>new_password</Text>
        //           <TextInput 
        //       style={styles.textinputstyle}
        //        placeholder="*********"
        //         keyboardtype="default"
                
        //         onChangeText={passwordhandler}
        //         value={enteredpassword}
        //         />
        //         <Text>Confirfpassword</Text>
        //         <TextInput 
        //       style={styles.textinputstyle}
        //        placeholder="*********"
        //         keyboardtype="default"
                
        //         onChangeText={passwordhandleragain}
        //         value={enteredpasswordagain}
        //         />
        //         {
        //             isloadingm?<ActivityIndicator/>:
                
        //         <Button onPress={resetpass} title="submit"/>
        //       }
        // </View>

       
        <View style={styles.main}>
            
        <LinearGradient
       // Background Linear Gradient
           colors={['#016129', '#016129']}
        //    start={[.9,.0]}
            style={{
              position: 'absolute',
              left: 0,
              right: 0,
              top: 0,
              height:"100%"
              }}
             />
        <ImageBackground  style={styles.backgroundimage} source={require("../assets/drawable-xxxhdpi/background.png")}>
  
       <View style={styles.dabba}>
          <Text style={styles.urnam}>Reset password</Text>
          <Text>Otp</Text> 
          <View style={styles.Vieweye}>
              <TextInput 
                style={styles.textinputstyle}
                placeholder="Otp"
                keyboardType={'numeric'}
                onChangeText={otphandler}
                value={enteredotp}/>
          </View>
         <Text>New password</Text>
         <View style={styles.Vieweye}>
              <TextInput 
                style={styles.textinputstyle}
                placeholder="New password"
                onChangeText={passwordhandler}
                value={enteredpassword}
                />
              </View>
                 <Text>Confirm New Password</Text>
                 <View style={styles.Vieweye}>
                  <TextInput 
                    style={styles.textinputstyle}
                    placeholder="Confirm New Password"
                    onChangeText={passwordhandleragain}
                    value={enteredpasswordagain}
                  />
                </View>
         <TouchableOpacity
         onPress={resetpass}
         >
         <LinearGradient
      // Button Linear Gradient
     
      colors={['#C53030', '#C53030']}
      start={[.9,.0]}
      style={{ padding: 15,
       alignItems: 'center',
        borderRadius: 5,
        width:200,
    
        marginLeft:60,
        marginTop:25 }}>
          {isloadingm?<ActivityIndicator/>:
      <Text
        style={{
          backgroundColor: 'transparent',
          fontSize: 16,
          color: '#fff',
          fontFamily:"Poppins-Bold"
        }}>
      Submit
      </Text>}
    </LinearGradient>
</TouchableOpacity>
     </View>
    </ImageBackground>
    </View>



    )
}
const styles=StyleSheet.create({
  main:{
    flex:1,
   // backgroundColor:"green"
},
logo:{
    backgroundColor:"white",
    height:72,
    width:72,
    justifyContent:"center",
    alignItems:'center',
    marginTop:90,
   marginLeft:158,
   borderRadius:40,
  
    
},
image:{
