    shadowOffset: { width: 0, height: 2 },
    shadowRadius: 8,
    elevation: 5,
    marginTop:210,
    paddingHorizontal:15,
    borderColor:"#5EB668",
    borderWidth:.4,
    paddingTop:10
},
welcome:{
    marginTop:20,
    color:'#1D2327',
    fontSize:18,
    fontFamily:"Poppins-Bold",
    justifyContent:"center",
    alignItems:"center"
},
urnam:{
    fontFamily:"Poppins-Regular",
    marginTop:6,
    color:'#1D2327',
    fontSize:16,
    textAlign:'center'
},
innput:{
    height:40,
    marginTop:20
},
logbutton:{
    marginTop:10,
    width:184,
    height:40,

},
bulogin:{
    fontSize:16,
    fontFamily:"Poppins-Bold",
    color:"#FFFFFF"
},
afterdabba:{
    flexDirection:"row",
    justifyContent:"center",
    marginTop:20
},
newuserr:{
    color:"#2F4858",
    fontSize:10,

},
signup:{
    color:'#00AF50',
    fontFamily:"Poppins-Regular",
    fontSize:10
},
forgatepaas:{
    marginTop:20,
    justifyContent:'center',
    alignItems:"center"
},
fp:{
     fontSize:15,
     color:'#2F4858',
     fontFamily:"Poppins-Light"
},
eye:{
  flexDirection:"row",

   alignItems:"center",

   borderWidth:.5,
   borderColor:'#ADEEB4',
   borderRadius:5,
   
},
textinputstyle:{
    height:50,
    paddingLeft:5
},
Vieweye:{
  borderWidth:.5,
  borderColor:'#9AE6B4',
  borderRadius:5,
}
});
export default Resetpassword;
