import React ,{useState,useEffect,}from 'react';
import {Text,View,StyleSheet,
    TouchableOpacity,Image,ImageBackground,
    TextInput,
    ScrollView,
    ActivityIndicator,
    Alert,
    Picker,
    Modal
} from 'react-native';
import Const from '../components/constant';
import UseTextInput from '../components/textinput';
//import PushNotification from 'react-native-push-notification'
import { LinearGradient } from 'expo-linear-gradient';
//import LinearGradient from 'react-native-linear-gradient';
import Ionicons from 'react-native-vector-icons/Ionicons';

import * as loginAction from '../store/action/auth';
import * as boarddetail from '../store/action/boardlist';
import Eye from '../assets/eye.tsx';
import {useSelector,useDispatch} from 'react-redux';


// PushNotification.configure({
//   // (optional) Called when Token is generated (iOS and Android)
//   onRegister: function (token) {
//     console.log("TOKEN:", token);
//   },

//   // (required) Called when a remote is received or opened, or local notification is opened
//   onNotification: function (notification) {
//     console.log("NOTIFICATION:", notification);

//     // process the notification

//     // (required) Called when a remote is received or opened, or local notification is opened
//    // notification.finish(PushNotificationIOS.FetchResult.NoData);
//   },

//   // (optional) Called when Registered Action is pressed and invokeApp is false, if true onNotification will be called (Android)
//   // onAction: function (notification) {
//   //   console.log("ACTION:", notification.action);
//   //   console.log("NOTIFICATION:", notification);

//   //   // process the action
//   // },

//   // (optional) Called when the user fails to register for remote notifications. Typically occurs when APNS is having issues, or the device is a simulator. (iOS)
//   // onRegistrationError: function(err) {
//   //   console.error(err.message, err);
//   // },

//   // IOS ONLY (optional): default: all - Permissions to register.
//   permissions: {
//     alert: true,
//     badge: true,
//     sound: true,
//   },

//   // Should the initial notification be popped automatically
//   // default: true
//   popInitialNotification: true,

//   /**
//    * (optional) default: true
//    * - Specified if permissions (ios) and token (android and ios) will requested or not,
//    * - if not, you must call PushNotificationsHandler.requestPermissions() later
//    * - if you are not using remote notification or do not have Firebase installed, use this:
//    *     requestPermissions: Platform.OS === 'ios'
//    */
//   requestPermissions: true,
// });
// testpush=()=>{PushNotification.localNotification({
 
//   title: "My Notification Title", // (optional)
//   message: "My Notification Message", // (require)
  
// })}
const Signup=({navigation})=>{
  
  const [modalVisible, setModalVisible] = useState(true);
    const [securer,setsecurer]=useState(false)
    // const [enteredschol0_id,setenteredschool_id]=useState("")
    // const [enteredboard_id,setenteredboard_id]=useState('');
    const [enteredmobile,setenteredmobile]=useState('');
    const [enteredPassword,setenteredpassword]=useState('');
    const [enteredname,setenteredname]=useState("");
    const [error,seterror]=useState();
    const [isloading, setisloading]=useState(false);
    const [isloadingm,setisloadingm]=useState(false);

    const [boardErr,setBoardErr]=useState(false)
    const [nameErr,setNameErr]=useState(false)
    const [mobileErr,setMobileErr]=useState(false)
    const [passwordErr,setPasswordErr]=useState(false)

    const dispatch=useDispatch();
    const [selectedValue, setSelectedValue] = useState(0);
    // console.log("*********************")
    // console.log("this is data needed"+enteredname +"***"+enteredPassword+"***"+enteredmobile+"****"+Const.school_id+"****"+selectedValue)
    // console.log("this is the selected value"+selectedValue)
    // console.log("**********************")
    const AuthHandler=async()=>{
      seterror(null)
      setisloading(true)

      if(enteredname == ""){
          setisloading(false)
          setNameErr(true);
      }else{
          setNameErr(false);
      }
      if(selectedValue == ""){
          setisloading(false)
          setBoardErr(true);
      }else{
          setBoardErr(false);
      }
      if(enteredmobile == ""){
          setisloading(false)
          setMobileErr(true);
      }else{
          setMobileErr(false);
      }
      if(enteredPassword == ""){
          setisloading(false)
          setPasswordErr(true);
      }else{
          setPasswordErr(false);
      }
      if(mobileErr || passwordErr || nameErr || boardErr){
          return;
      }

       try{
        await dispatch(loginAction.Signupp(Const.school_id,selectedValue,enteredmobile,enteredPassword,enteredname))
         navigation.navigate("Confirmation")
       }catch(error){
          seterror(error.message)
          // console.log("this is the eroor"+error)
          setisloading(false)
      }
    };
  
 const BoardList= async()=>{
      setisloadingm(true)
      try{
        await dispatch(boarddetail.board_list(Const.school_id))
        setisloadingm(false) 
      }catch(err){
      //  seterror(err.message)
      }
    }  

const data=useSelector(state=>state.Allboard.allboardlist)
    useEffect(() => {
        
        if (error) {
          Alert.alert('An Error Occurred!', error, [{ text: 'Okay' }]);
        }
        BoardList()
      }, [error]);
   
    const mobileinputhandler=(enteredText)=>{
        setenteredmobile(enteredText)
    }
    const passwordinputhandler=(enteredText)=>{
        setenteredpassword(enteredText);
    } 
    const nameinputhandler=(enteredText)=>{
        setenteredname(enteredText)
    }
    return(
        <ScrollView>
        <View style={styles.main}>
          <LinearGradient
           // Background Linear Gradient
              colors={['#016129', '#016129']}
              style={{
                  position: 'absolute',
                  left: 0,
                  right: 0,
                  top: 0,
                  height: 370,
                }}/>
      
            <ImageBackground  style={styles.backgroundimage} source={require("../assets/drawable-xxxhdpi/background.png")}>
           
           <View style={styles.dabba}>
                <Text style={styles.welcome}>Join millions of students sharing their experience</Text>
         
               <View style={{    height:40,
                    borderWidth:.5,
                    borderColor:'#ADEEB4',
                    borderRadius:5,  
                    paddingBottom:5
                  }}>
              
                    {isloadingm?<ActivityIndicator/>:      
                      <Picker
                        selectedValue={selectedValue}
                        style={{height:50 }}
                        onValueChange={(itemValue, itemIndex) => setSelectedValue(itemValue)}>
                        <Picker.Item label={" -select board- "} value={0} />
                        {data.map((dat,index)=> <Picker.Item  key={index} label={dat.name} value={dat.br_id} />)}
                      </Picker>
                    }
                 </View>
                 { boardErr == true ? (
                    <Text style={styles.errorMessage}>
                    * Select any board from lists.
                    </Text>
                    ) : null  }
            
              <Text style={styles.urnam}>Mobile*</Text>
            {/* <View style={styles.eye}> */}
              <TextInput 
              style={styles.textinputstyle} 
               placeholder="9923283721" 
               keyboardtype="default"
            
               onChangeText={mobileinputhandler}
               value={enteredmobile}
               />
               { mobileErr == true ? (
                <Text style={styles.errorMessage}>
                * Mobile number is required.
                </Text>
                ) : null  }
              <Text style={styles.urnam}>name*</Text>
              <TextInput 
              style={styles.textinputstyle}
               placeholder="Full Name"
                keyboardtype="default"
                
                onChangeText={nameinputhandler}
                value={enteredname}
                />
                { nameErr == true ? (
                    <Text style={styles.errorMessage}>
                    * Name is required.
                    </Text>
                    ) : null  }

              <Text style={styles.urnam}>Password</Text>
              <View style={styles.eye}>
                <TextInput 
                    style={{width:290,borderRightWidth:0,height:50}}
                    placeholder="password"
                    onChangeText={passwordinputhandler}
                    value={enteredPassword}
                    secureTextEntry={securer}
                />
                <TouchableOpacity onPress={()=>setsecurer(!securer)} style={{float:'right',marginLeft:-25, position:'relative'}}>
                  <View style={styles.eeeye} >
                    {securer?  <Ionicons name="md-eye-off" size={24} color="#C1C1C1" />:<Ionicons name="md-eye" size={24} color="#C1C1C1" />}
                  </View>   
                </TouchableOpacity>
              </View>
              { passwordErr == true ? (
                <Text style={styles.errorMessage}>
                * Password is required.
                </Text>
                ) : null  }
              <TouchableOpacity onPress={AuthHandler} style={{
                  position:'relative',
                  marginTop:0,
                  marginBottom:0,
                  marginLeft:'auto',
                  marginRight:'auto'
              }}>
                <LinearGradient
                  // Button Linear Gradient
                  colors={['#C53030', '#C53030']}
                  start={[.9,.0]}
                  style={{ padding: 10,
                  alignItems: 'center',
                    borderRadius: 5,
                    width:200,
                    marginTop:55 }}>
                  {isloading?<ActivityIndicator/>:(       
                  <Text
                    style={{
                      backgroundColor: 'transparent',
                      fontSize: 16,
                      color: '#fff',
                      fontFamily:"Poppins-Bold"
                    }}>
                  Submit
                  </Text>)}
                </LinearGradient>
    </TouchableOpacity>
         </View>
           <View style={styles.afterdabba}>
              <Text style={styles.newuserr}>Registered User?</Text>
              <Text style={styles.signup} onPress={()=>navigation.navigate('Login')}>Login</Text>
           </View>
        
           </ImageBackground>
           
        </View>
        </ScrollView>
    )
}
