import React,{useEffect,useState} from 'react';
import {View,Text,Button,StyleSheet, 
    Image,ImageBackground, Alert,
    ActivityIndicator
    
} from 'react-native';
import { Video } from 'expo-av';
import PDFReader from 'rn-pdf-reader-js';
import { ScrollView, TouchableOpacity } from 'react-native-gesture-handler';
import {useSelector,useDispatch} from 'react-redux';
import FontAwesome from 'react-native-vector-icons/FontAwesome';
import { LinearGradient } from 'expo-linear-gradient';
import Ionicons from "react-native-vector-icons/Ionicons";
import * as topicandvidd from '../store/action/topicandvideo';
import * as  donotallow from "../store/action/auth";

const MathScreen=({ route, navigation})=>{
    const { subject_id} = route.params;
    const {sub_namee}=route.params;
    console.log("this is the navigated subject id"+subject_id)
    console.log("this is the navigated subject id"+sub_namee)
    //const topicarray=useSelector(state=>state.Sub.topiclist)
    const [error,seterror]=useState()
    const [isloadingmm,setisloadingmm]=useState(false)
     const dispatch=useDispatch()
    const TopicandVideos= async()=>{
        seterror(null)
        //setisloadingm(true)
        try{
          await dispatch(topicandvidd.topicandvideo(subject_id))
          console.log("executed")
          //setisloadingm(false) 
        }catch(err){
          seterror(error.message)
          
        }
      }  

