import React,{useState,useEffect} from 'react';
import {Text,View,StyleSheet,
  TextInput,Button, Alert,ActivityIndicator,ScrollView,
  ImageBackground,
  TouchableOpacity
} from 'react-native';
import {useSelector,useDispatch} from 'react-redux';
import * as Forgatt from '../store/action/forgatepassword';
import { LinearGradient } from 'expo-linear-gradient';
import Const from '../components/constant';
import Resetpassword from './resetpassword';
const Forgatepassword=({navigation})=>{

    const dispatch=useDispatch()
    const [enteredmobilenumber,setenteredmobilenumber]=useState("");
    const [isloadingm ,setisloadingm]=useState(false)
    const[isloading,setisloading]=useState(false)
    const [error,seterror]=useState();
    const goalInputHandler5=(enteredText)=>{
        setenteredmobilenumber(enteredText)
    }


    const forgatepass= async()=>{
        seterror(null)
        setisloadingm(true)
        try{
          await dispatch(Forgatt.forgating(Const.school_id,enteredmobilenumber))
          console.log("executed")
          navigation.navigate(Resetpassword)
      
        }catch(error){
          seterror(error.message)
             setisloadingm(false) 
        }
      }  
      useEffect(() => {
        if (error) {
          Alert.alert('An Error Occurred!', error, [{ text: 'Okay' }]);
        }

      }, [error]);
    return(
       
        <View style={styles.main}>
            
        <LinearGradient
       // Background Linear Gradient
           colors={['#016129', '#016129']}
        //    start={[.9,.0]}
            style={{
              position: 'absolute',
              left: 0,
              right: 0,
              top: 0,
              height:"100%"
              }}/>
          <ImageBackground  style={styles.backgroundimage} source={require("../assets/drawable-xxxhdpi/background.png")}>
  
       <View style={styles.dabba}>
        
          <Text style={styles.urnam}>Enter registered mobile no.P</Text>
          <View style={styles.Vieweye}>
              <TextInput 
                style={styles.textinputstyle}
                placeholder="mobile number"
                keyboardType={'numeric'}
                onChangeText={goalInputHandler5}
                value={enteredmobilenumber}/>
           </View>
          
          <TouchableOpacity onPress={forgatepass} style={{
            position:'relative',
            marginTop:0,
            marginBottom:0,
            marginLeft:'auto',
            marginRight:'auto'
          }}>
          <LinearGradient
              // Button Linear Gradient
            colors={['#C53030', '#C53030']}
            start={[.9,.0]}
            style={{ padding: 10,
            alignItems: 'center',
              borderRadius: 5,
              width:200,
              marginTop:25 }}>
              {isloadingm?<ActivityIndicator/>:
            <Text
              style={{
                backgroundColor: 'transparent',
                fontSize: 16,
                color: '#fff',
                fontFamily:"Poppins-Bold"
              }}>
            Submit
            </Text>}
          </LinearGradient>
        </TouchableOpacity>
     </View>
    </ImageBackground>
    </View>


    )
}
const styles=StyleSheet.create({
        
          main:{
            flex:1,
           // backgroundColor:"green"
        },
        logo:{
            backgroundColor:"white",
            height:72,
            width:72,
            justifyContent:"center",
            alignItems:'center',
            marginTop:90,
           marginLeft:158,
           borderRadius:40,
          
            
        },
        image:{
            width:72,
            height:72,
            borderRadius:40,
            borderWidth:3,
            borderColor:"#F1F9FF"
        },
        user:{
            justifyContent:"center",
            alignItems:"center",
            marginTop:10
        },
        username:{
            fontSize:22,
            fontFamily:"Poppins-Bold",
            color:"#F1F9FF"
        },
        backgroundimage:{
        height:"120%"
        },
        dabba:{
            backgroundColor:"white",
            height:300,
            marginHorizontal:20,
            borderRadius:5,
            shadowColor: "black",
            shadowOpacity: 0.26,
            shadowOffset: { width: 0, height: 2 },
            shadowRadius: 8,
            elevation: 5,
            marginTop:210,
            paddingHorizontal:15,
            borderColor:"#5EB668",
            borderWidth:.4
        },
        welcome:{
            marginTop:20,
            color:'#1D2327',
            fontSize:18,
            fontFamily:"Poppins-Bold",
            justifyContent:"center",
            alignItems:"center"
        },
        urnam:{
            fontFamily:"Poppins-Regular",
          marginTop:6,
            color:'#1D2327',
            fontSize:14
        },
        innput:{
            height:40,
            marginTop:20
        },
        logbutton:{
            marginTop:10,
            width:184,
            height:40,
      
        },
        bulogin:{
            fontSize:16,
            fontFamily:"Poppins-Bold",
            color:"#FFFFFF"
        },
        afterdabba:{
            flexDirection:"row",
            justifyContent:"center",
            marginTop:20
        },
        newuserr:{
            color:"#2F4858",
            fontSize:10,
      
        },
        signup:{
            color:'#00AF50',
            fontFamily:"Poppins-Regular",
            fontSize:10
        },
        forgatepaas:{
            marginTop:20,
            justifyContent:'center',
            alignItems:"center"
        },
        fp:{
             fontSize:15,
             color:'#2F4858',
             fontFamily:"Poppins-Light"
        },
        eye:{
          flexDirection:"row",
       
           alignItems:"center",
       
           borderWidth:.5,
           borderColor:'#ADEEB4',
           borderRadius:5,
           
       },
        textinputstyle:{
            height:50,
            paddingLeft:5
        },
        Vieweye:{
          borderWidth:.5,
          borderColor:'#9AE6B4',
          borderRadius:5,
        }
});
export default Forgatepassword;
