      marginTop:20
  },
  logbutton:{
      marginTop:10,
      width:184,
      height:40,

  },
  butto:{
      marginTop:30,
      width:100,
      marginLeft:110

  },
  bulogin:{
      fontSize:16,
      fontFamily:"Poppins-Bold",
      color:"#FFFFFF"
  },
  afterdabba:{
      flexDirection:"row",
      justifyContent:"center",
      marginTop:20
  },
  newuserr:{
      color:"#1D2327",
      fontSize:10,

  },
  signup:{
      color:'#007E59',
      fontFamily:"Poppins-Regular",
      fontSize:10
  },
  forgatepaas:{
      marginTop:20,
      justifyContent:'center',
      alignItems:"center"
  },
  fp:{
       fontSize:15,
       color:'#1D2327',
       fontFamily:"Poppins-Light"
  },
  eye:{
    flexDirection:"row",
    alignItems:"center",
    borderWidth:.5,
    borderColor:'#C6F6D5',
    borderRadius:5
  },
  eeeye:{
   paddingRight:10
  },
