import React from 'react';
import {Text,View,StyleSheet ,Button ,Image} from 'react-native';

const SubShow=props=>{
    return (
           <View style={{...styles.main, ...props.style}}> 
               <View style={styles.first}>
                    <Image source={props.source} style={{}}/>
               </View>
              <View style={styles.second}>
                   <View>
                      <Text style={{color:props.subcolor,
                                fontFamily:"Rubik-Bold",
                                fontSize:20,
                                marginBottom:10
                         }}>
                          {props.subject.substring(0,7)}
                          </Text>
                   </View>
                   <View style={styles.notess}>
                   <Text style={{color:props.notecolor,
                           fontFamily:"Rubik-Regular",
                           fontSize:10,
                    
                    }}>Notes - </Text>
                   <Text  style={{color:props.notecolor,
                           fontFamily:"Rubik-Bold",
                           fontSize:10
                    }}>{props.noofnotes}</Text>
                   </View>
                   <View style={styles.notess}>
                   <Text  style={{color:props.notecolor,
                           fontFamily:"Rubik-Regular",
                           fontSize:10
                    }}>Video - </Text>
                   <Text  style={{color:props.notecolor,
                           fontFamily:"Rubik-Bold",
                           fontSize:10
                    }}>{props.noofvideo}</Text>
                    </View>
                </View>
               <View style={styles.third}>
               <Text style={{color:props.timingcolor,
                       fontFamily:"Rubik-Bold",
                       fontSize:18,
                   textAlign:"right"
              }}>{props.timing}</Text>
               <Text style={{
                   color:props.timingcolor,
                   fontFamily:"Rubik-Medium",
                   fontSize:10,
              
                   textAlign:"right"
                   
               }}
               selectable={true}
               >Live Session</Text>
               <Text style={{
                   color:props.livesessioncolor,
                   fontFamily:"Rubik-Regular",
                   fontSize:8,
                   marginTop:20,
                   textAlign:"right"
               }}>{props.nextlivesession}</Text>
               </View>
           </View>
    )
}
const styles=StyleSheet.create({
   main:{
       flexDirection:"row",
       paddingHorizontal:10,
       paddingVertical:10,
       height:110,
       marginHorizontal:25,
       marginTop:10,
       borderRadius:10,
       alignItems:"center",
       borderWidth:.3,
   },
   first:{
    marginLeft:7,
    width:70,
    height:70,
    borderRadius:40,
    backgroundColor:"white",
    alignItems:"center",
    justifyContent:"center"
   },
   second:{
    marginLeft:20,
    width:"25%",
   },
   third:{
    marginLeft:20,
   },
   notess:{
       flexDirection:'row'
   }
});
export default SubShow;
